<?phpif(!defined('IN_MOBILE_API')) {	exit('Access Denied');}$upid = $_GET['upid'];$sql = "SELECT * FROM `newultrax`.`pre_1hand_equipment_class` where level='2' and upid = '$upid'";$query = DB::QUERY($sql);$data = array();while($row = DB::fetch($query)) {				$data[] = cellapp_core::getvalues($row, array('id', 'ename', 'cname', 'level', 'upid', 'pic'));			}$variable = array('data' => $data);cellapp_core::result(cellapp_core::variable($variable));?>